var widgetId = $api.byId('widgetId').value;
api.openWidget({
    id: widgetId,
    wgtParam: {name:'test'},
    animation: {
        type: 'flip',
        subType: 'from_bottom',
        duration: 500
    }
}, function(ret, err){
if (ret) {
        api.alert({
            title: 'widget',
            msg: '录入'
        });
    } else{
        api.alert({
            title: 'widget',
            msg: err.msg
        });
    };

});
